mapper.select:
select finishadflag
from adstatusofuser
where userUnique=#{userUnique}
and period=#{currentPeriod}

select USERUNIQUE,PERIOD,FINISHADFLAG as finishAdvertiseFlag,FINISHORDERFLAG,CHOOSEORDERFLAG from adstatusofuser where userunique in
#{item}
and period=#{period}

select USERUNIQUE,PERIOD,FINISHADFLAG
as finishAdvertiseFlag,FINISHORDERFLAG,CHOOSEORDERFLAG
from adstatusofuser
where userunique=#{userUnique}
and period=#{period}

mapper.update:
update adstatusofuser
set finishadflag=#{finishFlag}
where userunique=#{userUnique}
and period=#{period}

update adstatusofuser
set finishorderflag=#{finishFlag}
where userunique=#{userUnique}
and period=#{period}

update.foreach:
update adstatusofuser

    chooseOrderFlag=#{chooseOrderFlag}

where userunique=#{item} and period =#{period}
