mapper.select:
select RESEARCHPERIOD,RESEARCHCOST,BEGINTIME,STATUS
from DEVELOPINGISO
where USERUNIQUE=#{userUnique}
and ISONAME=#{isoName}

mapper.update:
update developingiso
set STATUS =#{status}
where USERUNIQUE = #{userUnique}
and ISONAME =#{isoName}

mapper.delete:
delete
from developingiso
where userunique = #{userUnique}
and isoname = #{isoName}
