SELECT ORDERID,USERUNIQUE,MATERIALNAME, MNUMBER,WAREHOUSENAME,HAPPENTIME,ENDTIME
FROM BUYINGMATERIAL
WHERE userUnique=#{userUnique}
AND ENDTIME <= #{currentTime}
order by ORDERID
desc limit  #{start} , #{end}


